/**
 * ÌöåÏõê ÌÉàÌá¥ Í∏∞Îä•
 * - ÎπÑÎ∞ÄÎ≤àÌò∏/Íµ¨Í∏Ä Í≥ÑÏ†ï Î∞©ÏãùÏóê Îî∞Îùº Ïû¨Ïù∏Ï¶ùÏùÑ ÏàòÌñâÌïòÍ≥†
 * - Ìï¥Îãπ ÏÇ¨Ïö©ÏûêÏùò ÌöåÍ≥†(retrospectives) Î¨∏ÏÑúÎ•º Î™®Îëê ÏÇ≠Ï†úÌïòÍ≥†
 * - FirestoreÏùò users Î¨∏ÏÑúÎ•º ÏÇ≠Ï†úÌïú Îí§
 * - Firebase Auth Í≥ÑÏ†ï ÏûêÏ≤¥Î•º ÏÇ≠Ï†úÌïúÎã§.
 * Ïò§Î•ò ÏÉÅÌô©Ïóê Îî∞Îùº Îã§ÏñëÌïú ÌÜ†Ïä§Ìä∏ Î©îÏãúÏßÄÎ•º Ï∂úÎ†•ÌïúÎã§.
 */

export async function deleteUserAccount(user: any, password?: string) {
	try {
		const userId = user.uid;
		const providerId = user.providerData[0]?.providerId;

		if (providerId === 'password') {
			if (!password) {
				throw new Error('ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÌïÑÏöîÌï©ÎãàÎã§.');
			}
			const credential = EmailAuthProvider.credential(user.email, password);
			await reauthenticateWithCredential(user, credential);
		} else if (providerId === 'google.com') {
			const googleProvider = new GoogleAuthProvider();
			await reauthenticateWithPopup(user, googleProvider);
		}

		const retrospectivesRef = collection(db, 'retrospectives');
		const q = query(retrospectivesRef, where('userId', '==', userId));
		const querySnapshot = await getDocs(q);

		const deletePromises = querySnapshot.docs.map((docSnap) => deleteDoc(docSnap.ref));
		await Promise.all(deletePromises);

		await deleteDoc(doc(db, 'users', userId));

		await deleteUser(user);

		customGoodBye();
		return { success: true };
	} catch (error: any) {
		console.error('ÌöåÏõê ÌÉàÌá¥ Ïò§Î•ò:', error);
		if (error.code === 'auth/requires-recent-login') {
			errorNeedReLogin();
		} else if (error.code === 'auth/invalid-credential') {
			errorInvalidPassword();
		} else if (
			error.code === 'auth/cancelled-popup-request' ||
			error.code === 'auth/popup-closed-by-user'
		) {
			errorCancelledPopup();
		} else if (error.code === 'auth/user-mismatch') {
			errorMismatchUser();
		} else {
			errorDeleteAccount();
		}
		return { success: false, error };
	}
}

